n      //this.cdr.markForCheck();\\n    });\\n  }\\n\\n  protected feedIdWithLabelIfNotSet() {\\n    if (this.projectedNgControl && this.projectedNgControlRef) {\\n      this.contentControlId =\\n        this.projectedNgControlRef.nativeElement.getAttribute(\'id\');\\n      if (!this.contentControlId) {\\n        this.contentControlId = `${this.projectedNgControl.name}`;\\n        if (!this.contentControlId) {\\n          console.warn(\\n            `FormControl sans nom :`,\\n            this.projectedNgControlRef.nativeElement\\n          );\\n        }\\n        this.projectedNgControlRef?.nativeElement.setAttribute(\\n          \'id\',\\n          this.contentControlId\\n        );\\n      }\\n    }\\n  }\\n\\n  protected a11yConnectLabelForElement() {\\n    const labelComponent =\\n      this.projectedLabelComponent ?? this.generatedZacLabelComponent;\\n    let boundElement;\\n    if (this.projectedNgControlRef) {\\n      if (labelComponent) {\\n        if (\\n          labelComponent.forElement !==\\n            this.projectedNgControlRef.nativeElement ||\\n          labelComponent.ngControl !== this.projectedNgControl\\n        ) {\\n          labelComponent.connectToControl(\\n            this.projectedNgControlRef.nativeElement,\\n            this.projectedNgControl\\n          );\\n        }\\n\\n        boundElement = this.connectLabelToBoundElement(labelComponent);\\n      }\\n      this.a11yHint(boundElement);\\n    }\\n  }\\n\\n  /**\\n   * Fait le lien entre le labelComponent et l\'élément de formulaire projeté.\\n   * @param labelComponent\\n   */\\n  protected connectLabelToBoundElement(labelComponent: ZacLabelComponent) {\\n    if (this.projectedNgControlRef) {\\n      return this.labelForInputService.tryToConnectNonNativeFormControlElementToZacLabelElement(\\n        this.projectedNgControlRef.nativeElement,\\n        labelComponent.elementRef.nativeElement\\n      );\\n    }\\n    return undefined;\\n  }\\n\\n  // Si on passe en readonly, en prin